import "./App.css";
import { useState, useEffect } from "react";
import {
  ref,
  uploadBytes,
  getDownloadURL,
  listAll,
  list,
} from "firebase/storage";
import { storage } from "./firebase";
import { v4 } from "uuid";

function App() {
  const [imageUpload, setImageUpload] = useState(null);
  const [imageUrls, setImageUrls] = useState([]);

  const imagesListRef = ref(storage, "images/");
  const uploadFile = () => {
    if (imageUpload == null) return;
    const imageRef = ref(storage, `images/${imageUpload.name + v4()}`);
    uploadBytes(imageRef, imageUpload).then((snapshot) => {
      getDownloadURL(snapshot.ref).then((url) => {
        setImageUrls((prev) => [...prev, url]);
      });
    });
  };

  useEffect(() => {
    listAll(imagesListRef).then((response) => {
      response.items.forEach((item) => {
        getDownloadURL(item).then((url) => {
          setImageUrls((prev) => [...prev, url]);
        });
      });
    });
  }, []);

   // <input
   //      type="file"
   //      onChange={(event) => {
   //        setImageUpload(event.target.files[0]);
   //      }}
   //    />
   //    <button onClick={uploadFile}> Upload Image</button>
   //    <div class="slider-container">
   //      {imageUrls.map((url) => {
   //        return <img class="slider-item" src={url} />;
   //      })}
   //    </div> 

  return (
    <div className="App">
      <div class="container">

        <ul class="slider">
          <li id="slide1">
            <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/3947459/car.jpg"/>
          </li>
          <li id="slide2">
            <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/3947459/sunset.jpg"/>
          </li>
          <li id="slide3">
            <h1>Ejemplo con otros elementos</h1>
            <p>Esto es un párrafo de ejemplo para comprobar que podemos meter cualquier tipo de elementos en el slider</p>
            <a href="https://kikopalomares.com/">¡Corre a mi web para más contenido!</a>
          </li>
        </ul>

        <ul class="menu">
          <li>
            <a href="#slide1">1</a>
          </li>
          <li>
            <a href="#slide2">2</a>
          </li>
           <li>
            <a href="#slide3">3</a>
          </li>
        </ul>

      </div>
    </div>
  );
}

export default App;
